package day3;

public class ThucHanh_If_Else_For {
	public static void cacSoChiaHetCho5NhoHon100() {
		System.out.println("Cac so chia het cho 5 va nho hon 100 la: ");
		for(int i = 0; i<= 100; i++) {
			if(i%5==0) {
				System.out.print(i + "    ");
			}
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		cacSoChiaHetCho5NhoHon100();

	}

}
